@charset "UTF-8";
* {
  padding: 0;
  margin: 0;
  z-index: 1;
  font-family: "Noto Sans KR", sans-serif;
}

li {
  list-style: none;
}

a {
  text-decoration: none;
  color: #000;
}

.warp {
  position: relative;
}
.warp #QuickMenu {
  z-index: 9999;
  position: fixed;
  right: 10px;
  top: 50%;
  transform: translateY(-50%);
  width: 50px;
  height: 250px;
  box-sizing: border-box;
  background-color: #fff;
  border-radius: 5px;
  border: 1px solid #eee;
  box-shadow: 3px 3px 5px #000;
}
.warp #QuickMenu .QuickMenu_box {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
}
.warp #QuickMenu .QuickMenu_box .Q_menu {
  width: 50px;
  height: 50px;
  border-bottom: 1px solid #ccc;
  box-sizing: border-box;
  padding: 5px;
}
.warp #QuickMenu .QuickMenu_box .Q_menu img {
  width: 100%;
  height: 100%;
  display: block;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_My,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct {
  width: 150px;
  overflow: hidden;
  padding: 0;
  display: flex;
  cursor: pointer;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickMy_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickDeliveryCheck_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickCart_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickViewProduct_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickMy_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickDeliveryCheck_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickCart_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickViewProduct_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickMy_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickDeliveryCheck_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickCart_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickViewProduct_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickMy_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickDeliveryCheck_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickCart_txt,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickViewProduct_txt {
  width: 0px;
  font-size: 14px;
  line-height: 50px;
  text-align: center;
  cursor: pointer;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickMy_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickDeliveryCheck_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickCart_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickViewProduct_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickMy_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickDeliveryCheck_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickCart_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickViewProduct_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickMy_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickDeliveryCheck_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickCart_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickViewProduct_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickMy_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickDeliveryCheck_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickCart_img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickViewProduct_img {
  width: 50px;
  padding: 5px;
  box-sizing: border-box;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickMy_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickViewProduct_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickMy_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickViewProduct_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickMy_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickViewProduct_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickMy_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickViewProduct_img img {
  width: 47px;
  height: auto;
  display: block;
  margin-left: -11%;
  margin-top: -5%;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_My .QuickViewProduct_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_DeliveryCheck .QuickViewProduct_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_Cart .QuickViewProduct_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickDeliveryCheck_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickCart_img img,
.warp #QuickMenu .QuickMenu_box .QuickMenu_ViewProduct .QuickViewProduct_img img {
  width: 39px;
  margin: 0;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_UP_Down {
  position: absolute;
  bottom: 0;
  width: 50px;
  height: 50px;
  line-height: 25px;
  font-size: 18px;
  border-bottom: none;
  padding: 0;
  text-align: center;
  color: #777;
  overflow: hidden;
  cursor: pointer;
  box-sizing: border-box;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_UP_Down .QuickUp {
  width: 100%;
  height: 50%;
  border-bottom: 1px solid #ccc;
  box-sizing: border-box;
  overflow: hidden;
}
.warp #QuickMenu .QuickMenu_box .QuickMenu_UP_Down .QuickUp a,
.warp #QuickMenu .QuickMenu_box .QuickMenu_UP_Down .QuickDown a {
  color: #777;
  display: block;
}
.warp .Q_menu {
  width: 50px;
  height: 50px;
}

/* Header =================================== */
#header {
  position: absolute;
  left: 0;
  top: 0;
  z-index: 10;
  width: 100%;
  height: 100px;
  background-color: #fff;
}
#header .header_outer {
  width: 100%;
  height: 100px;
}
#header .header_box {
  display: flex;
  width: 1660px;
  height: 100px;
  margin: 0 auto;
  flex-direction: row;
  justify-content: space-between;
  margin-top: 0.5px;
}
#header .header_box .logo {
  width: 150px;
  height: 100px;
  line-height: 105px;
  text-align: center;
  padding: 10px;
  box-sizing: border-box;
}
#header .header_box .sht_header .each_menu_ul,
#header .header_box .mainMenu .mainMenu_ul {
  width: 400px;
  height: 100px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 20px;
  font-weight: bold;
}
#header .header_box .sht_header .each_menu_ul li:nth-child(4),
#header .header_box .mainMenu .mainMenu_ul li:nth-child(4) {
  width: 1px;
  height: 15px;
  border-right: 1px solid #000;
  box-sizing: border-box;
}
#header .header_box .sht_header .each_menu_ul li,
#header .header_box .mainMenu .mainMenu_ul li {
  height: 100px;
  line-height: 100px;
}
#header .header_box .sht_header .each_menu_ul a,
#header .header_box .mainMenu .mainMenu_ul a {
  color: #000;
  display: block;
}

/* GNB ========== */
#header .gnb .gnb_box .gnb_Menu {
  width: 220px;
  height: 100px;
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
}
#header .gnb .gnb_box .gnb_Menu .gnb_Menu_map picture {
  padding: 5px;
  text-align: center;
}

/* 메인 메뉴 SHEET HEADER 영역*/
.mainMenu_Sheet {
  position: absolute;
  top: 0.5px;
  left: 0;
  z-index: 100;
  width: 100%;
  height: 105vh;
  display: flex;
  justify-content: center;
  /* background 블러처리 */
  background-color: rgba(0, 0, 0, 0.1);
  -webkit-backdrop-filter: blur(8px);
          backdrop-filter: blur(8px);
}
.mainMenu_Sheet .main_menu_sht_outer {
  width: 100%;
  height: 65vh;
  background-color: #fff;
  display: flex;
  justify-content: center;
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht {
  display: flex;
  z-index: 100;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: center;
  width: 1660px;
  height: 65vh;
  background-color: #fff;
  background-blend-mode: hue;
  /* SHEET 메뉴 영역 */
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht .sht_header {
  display: flex;
  width: 1660px;
  height: 100px;
  margin: 0 auto;
  flex-direction: row;
  justify-content: space-between;
  box-sizing: border-box;
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht .sht_header .sht_logo {
  width: 150px;
  height: 100px;
  line-height: 105px;
  text-align: center;
  padding: 10px;
  box-sizing: border-box;
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht .sht_header .each_menu_ul {
  width: 400px;
  height: 100px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 20px;
  font-weight: bold;
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht .sht_header .each_menu_ul li {
  height: 25px;
  line-height: 25px;
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht .sht_header .each_menu_ul li:hover {
  border-bottom: 2px solid #002053;
  box-sizing: border-box;
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht .sht_header .sht_gnb_Menu {
  width: 220px;
  height: 100px;
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
}
.mainMenu_Sheet .main_menu_sht_outer .main_menu_sht .sht_header .sht_gnb_Menu .sht_gnb_Menu_map picture {
  padding: 5px;
  text-align: center;
}

/* SHEET 영역 내 HEADER li(bar) 영역*/
.mainMenu_Sheet .main_menu_sht .sht_header .each_menu_ul li a {
  color: #000;
}
.mainMenu_Sheet .main_menu_sht .sht_header .each_menu_ul li:nth-child(4) {
  position: relative;
  z-index: -1;
  display: block !important;
  width: 1px;
  height: 15px;
  border-right: 1px solid #000;
  box-sizing: border-box;
}

/* SHEET MENU LIST BOX (맨앞,맨뒤영역)*/
.sheet_box {
  width: 1660px;
  position: absolute;
  top: 100px;
  display: flex;
}
.sheet_box .sht_firstBox,
.sheet_box .sht_lastBox {
  display: flex;
  width: 13.3333%;
  height: 500px;
  flex-direction: row;
  flex-wrap: wrap;
  box-sizing: border-box;
  align-content: flex-start;
  padding: 30px 20px;
  box-sizing: border-box;
}
.sheet_box .sht_firstBox li:first-child,
.sheet_box .sht_lastBox li:first-child {
  width: 100%;
  height: 50px;
  line-height: 35px;
  font-weight: bold;
  font-size: 18px;
}
.sheet_box .sht_firstBox li:nth-child(6),
.sheet_box .sht_lastBox li:nth-child(6) {
  margin-top: 20px;
}
.sheet_box .sht_firstBox li,
.sheet_box .sht_lastBox li {
  width: 100%;
  height: 35px;
  line-height: 35px;
}
.sheet_box .sht_firstBox li:hover a,
.sheet_box .sht_lastBox li:hover a {
  color: #002053;
  font-weight: bold;
}
.sheet_box .sht_firstBox li:first-child:hover a,
.sheet_box .sht_lastBox li:first-child:hover a {
  color: #000;
}

/* SHEET MENU LIST BOX (중간영역)*/
.sht_box_1,
.sht_box_2,
.sht_box_3,
.sht_box_4 {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-content: flex-start;
  width: 13.3333%;
