sources.CSS\\detailPage.scss:
* {
  padding: 0;
  margin: 0;
  z-index: 1;
  font-family: 'Noto Sans KR', sans-serif;
}

li {
  list-style: none;
}

a {
  text-decoration: none;
  color: #000;
}



// ======================
.warp {
  position: relative;

  #QuickMenu {
    z-index: 9999;
    position: fixed;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    width: 50px;
    height: 250px;
    box-sizing: border-box;
    background-color: #fff;
    border-radius: 5px;
    border: 1px solid #eee;
    box-shadow: 3px 3px 5px #000;

    .QuickMenu_box {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;

      .Q_menu {
        width: 50px;
        height: 50px;
        border-bottom: 1px solid #ccc;
        box-sizing: border-box;
        padding: 5px;

        img {
          width: 100%;
          height: 100%;
          display: block;
        }
      }
      .QuickMenu_My,
      .QuickMenu_DeliveryCheck,
      .QuickMenu_Cart,
      .QuickMenu_ViewProduct {
        width: 150px;
        overflow: hidden;
        padding: 0;
        display: flex;
        cursor: pointer;

        .QuickMy_txt,
        .QuickDeliveryCheck_txt,
        .QuickCart_txt,
        .QuickViewProduct_txt {
          width: 0px;
          font-size: 14px;
          line-height: 50px;
          text-align: center;
          cursor: pointer;
        }
        .QuickMy_img,
        .QuickDeliveryCheck_img,
        .QuickCart_img,
        .QuickViewProduct_img {
          width: 50px;
          padding: 5px;
          box-sizing: border-box;

          img {
            width: 47px;
            height: auto;
            display: block;
            margin-left: -11%;
            margin-top: -5%;
          }
        }
        .QuickDeliveryCheck_img img,
        .QuickCart_img img,
        .QuickViewProduct_img img {
          width: 39px;
          margin: 0;
        }
      }




      .QuickMenu_UP_Down {
        position: absolute;
        bottom: 0;
        width: 50px;
        height: 50px;
        line-height: 25px;
        font-size: 18px;
        border-bottom: none;
        padding: 0;
        text-align: center;
        color: #777;
        overflow: hidden;
        cursor: pointer;
        box-sizing: border-box;

        .QuickUp {
          width: 100%;
          height: 50%;
          border-bottom: 1px solid #ccc;
          box-sizing: border-box;
          overflow: hidden;
        }
        .QuickUp a,
        .QuickDown a {
          color: #777;
          display: block;
        }
      }
    }
  }

    .Q_menu {
      width: 50px;
      height: 50px;
    }
  }







/* Header =================================== */

$PointColor: #002053;
$SmallFont : 14px;
$MidleFont : 16px;
$LageFont : 18px;
$TitFont : 32px;

#header {
  position: absolute;
  left: 0;
  top: 0;
  z-index: 10;
  width: 100%;
  height: 100px;
  background-color: #fff;

  .header_outer{
    width: 100%;
    height: 100px;
  }

  .header_box {
    display: flex;
    width: 1660px;
    height: 100px;
    margin: 0 auto;
    flex-direction: row;
    justify-content: space-between;
    margin-top: 0.5px;

    .logo {
      width: 150px;
      height: 100px;
      line-height: 105px;
      text-align: center;
      padding: 10px;
      box-sizing: border-box;
    }

    .sht_header .each_menu_ul,
    .mainMenu .mainMenu_ul {
      width: 400px;
      height: 100px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      font-size: 20px;
      font-weight: bold;

      li:nth-child(4) {
        width: 1px;
        height: 15px;
        border-right: 1px solid #000;
        box-sizing: border-box;
      }

      li {
        height: 100px;
        line-height: 100px;
      }

      a {
        color: #000;
        display: block;
      }
    }
  }
}


/* GNB ========== */
#header .gnb {
  .gnb_box {
    .gnb_Menu {
      width: 220px;
      height: 100px;
      display: flex;
      flex-wrap: wrap;
      flex-direction: row;
      justify-content: space-around;
      align-items: center;

      .gnb_Menu_map picture {
        padding: 5px;
        text-align: center;
      }
    }
  }
}



/* 메인 메뉴 SHEET HEADER 영역*/
.mainMenu_Sheet {
  position: absolute;
  top: 0.5px;
  left: 0;
  z-index: 100;
  width: 100%;
  height: 105vh;
  display: flex;
  justify-content: center;
  /* background 블러처리 */
  background-color: rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(8px);

  .main_menu_sht_outer {
    width: 100%;
    height: 65vh;
    background-color: #fff;
    display: flex;
    justify-content: center;

    .main_menu_sht {
      display: flex;
      z-index: 100;
      flex-direction: row;
      flex-wrap: wrap;
      justify-content: center;
      width: 1660px;
      height: 65vh;
      background-color: #fff;
      background-blend-mode: hue;

      /* SHEET 메뉴 영역 */
      .sht_header {
        display: flex;
        width: 1660px;
        height: 100px;
        margin: 0 auto;
        flex-direction: row;
        justify-content: space-between;
        box-sizing: border-box;

        .sht_logo {
          width: 150px;
          height: 100px;
          line-height: 105px;
          text-align: center;
          padding: 10px;
          box-sizing: border-box;
        }

        .each_menu_ul {
          width: 400px;
          height: 100px;
          display: flex;
          justify-content: space-between;
          align-items: center;
          font-size: 20px;
          font-weight: bold;

          li {
            height: 25px;
            line-height: 25px;
          }

          // sheet 메뉴 상단 hover color 변경
          li:hover {
            border-bottom: 2px solid $PointColor;
            box-sizing: border-box;
          }
        }

        .sht_gnb_Menu {
          width: 220px;
          height: 100px;
          display: flex;
          flex-wrap: wrap;
          flex-direction: row;
          justify-content: space-around;
          align-items: center;

          .sht_gnb_Menu_map picture {
            padding: 5px;
            text-align: center;
          }
        }
      }
    }
  }
}


/* SHEET 영역 내 HEADER li(bar) 영역*/
.mainMenu_Sheet {
  .main_menu_sht {
    .sht_header {
      .each_menu_ul {
        li {
          a {
            color: #000;
          }
        }

        li:nth-child(4) {
          position: relative;
          z-index: -1;
          display: block !important;
          width: 1px;
          height: 15px;
          border-right: 1px solid #000;
          box-sizing: border-box;
        }
      }
    }
  }
}



