          }
          li:nth-child(2),
          li:nth-child(6) {
            width: 1px;
            height: 12px;
            margin: 0 10px;
            margin-top: 7px;
            border-right: 1px solid #aaa;
            box-sizing: border-box;
          }


          li:nth-child(5) {
            margin-right: 0;
            font-weight: normal;
            font-size: 14px;
            float: right;
          }

          li:nth-child(6) {
            float: right;
          }

          li:nth-child(7) {
            margin-right: 0;
            font-weight: normal;
            font-size: 14px;
            float: right;
          }
        }
      }

      .ReviewStar {
