.QnA {
  
  .QnA_Top {

    .QnA_Top_Right {
      display: flex;
      justify-content: space-between;
      
      input {display: none;}
      label {
        display: inline-block;
        width: 205px;
        height: 40px;
        line-height: 35px;
        background-color: #fff;
        text-align: center;
        border-radius: 30px;
        color: #000;
        border: 2px solid $PointColor;
        cursor: pointer;
        box-sizing: border-box;
        font-weight: bold;
        cursor: pointer;
      }
      label:hover {
        border: 2px solid $PointColor;
        color: #fff;
        background-color: $PointColor;
        transition: 0.3s;
        box-sizing: border-box;
      }
    }
  }

  .QnA_Content {
    width: 1420px;
    height: 500px;
    padding: 20px;
    box-sizing: border-box;
    
    .QnA_Content_box {
      width: 1380px;
      height: 460px;

      .QnA_Content_MenuList {
        position: relative;
        width: 1380px;
        height: 35px;
        padding: 2.5px;
        box-sizing: border-box;
        background-color: #eee;
        float: left;

        li {
          width: 180px;
          height: 35px;
          line-height: 35px;
          text-align: center;
          float: left;
        }
        li:nth-child(2),
        li:nth-child(4) {
          width: 1px;
          height: 15px;
          border-right: 1px solid #000;
          margin-top: 9px;
          box-sizing: border-box;
        }


        li:nth-child(5) {
          width: 1010px;
          float: left;
        }

      }
    }

    .Create_div_QnABox {
      width: 100%;
      height: 75px;
      float: left;
      border-bottom: 1px solid #ddd;
      box-sizing: border-box;

      .Create_div_QnA1,
      .Create_div_QnA2,
      .Create_div_QnA3 {
        width: 179px;
        height: 75px;
        line-height: 50px;
        float: left;
        margin-right: 5px;
        padding: 12px;
        box-sizing: border-box;
        text-align: center;
      }
      .Create_div_QnA3 {
        width: 1005px;
        text-align: left;
        line-height: 25px;
        padding: 13px;

        p {
          width: 91%;
          float: left;
        }

        .QnADel_del {
          float: right;
          margin-top: 10px;
          font-size: 14px;
          cursor: pointer;
          color: #777;
        }
      }
    }
  }


  .QnA_Text {
    display: none;
    z-index: 10;
    position: absolute;
    top: 50px;
    left: 50%;
    transform: translateX(-50%);
    width: 500px;
    height: 400px;
    background-color: rgba(255, 255, 255, 0.3);
    backdrop-filter: blur(3px);
    padding: 20px;
    border-radius: 5px;
    border: 1px solid #ddd;
    box-sizing: border-box;

    .QnA_TextBox {
      width: 460px;
      height: 360px;
      background-color: #eee;
      padding: 20px;
      box-sizing: border-box;
      border-radius: 5px;

      textarea {
        display: block;
        width: 420px;
        height: 200px;
        resize: none;
        word-wrap: break-word;
        box-sizing: border-box;
        padding: 10px;
        cursor: pointer;
      }
      .QnA_Name_box {
        margin-top: 7px;
        width: 170px;

        h1{
          text-align: center;
          font-size: 18px;
        }

        #QnA_Name {
          width: 170px;
          margin: 0 auto;
          outline: none;
          text-align: center;
          border-radius: 5px;
          border: 1px solid #777;
          padding: 3px;
          margin-top: 2px;
          cursor: pointer;
        }
      }

      .QnA_Phone_box {
        width: 170px;
        margin-top: 7px;

        h1 {
          text-align: center;
          font-size: 18px;
        }

        #QnA_Phone {
          width: 170px;
          outline: none;
          text-align: center;
          border-radius: 5px;
          border: 1px solid #777;
          margin-top: 2px;
          padding: 3px;
          cursor: pointer;
        }
      }

      input#QnA_Button {
        display: none;
      }
      label {
        position: absolute;
        bottom: 40px;
        right: 40px;
        width: 200px;
        height: 50px;
        line-height: 50px;
        border-radius: 30px;
        display: block;
        background-color: #fff;
        text-align: center;
        font-size: 14px;
        border: 1px solid #000;
        box-sizing: border-box;
        transition: 0.3s;
        cursor: pointer;
      }
      label:hover {
        background-color: $PointColor;
        color: #fff;
        border: 1px solid $PointColor;
      }

      .QnAClose {
        position: absolute;
        top: 0px;
        right: 0px;
        width: 45px;
        height: 45px;
        background-color: #fff;
        border-radius: 10px;

        input {display: none;}
        label {
          position: absolute;
          top: 7px;
          right: 7px;
          width: 30px;
          height: 30px;
          display: block;
          padding: 0;
          margin: 0;
          text-align: center;
          line-height: 28px;
          border: none;
          box-sizing: border-box;
          cursor: pointer;
        }
      }
    }
  }
}































