* {
  padding: 0;
  margin: 0;
  z-index: 1;
  font-family: 'Noto Sans KR', sans-serif;
}

li {
  list-style: none;
}

a {
  text-decoration: none;
  color: #000;
}



// ======================
.warp {
  position: relative;

  #QuickMenu {
    z-index: 9999;
    position: fixed;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    width: 50px;
    height: 250px;
    box-sizing: border-box;
    background-color: #fff;
    border-radius: 5px;
    border: 1px solid #eee;
    box-shadow: 3px 3px 5px #000;

    .QuickMenu_box {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;

      .Q_menu {
        width: 50px;
        height: 50px;
        border-bottom: 1px solid #ccc;
        box-sizing: border-box;
        padding: 5px;

        img {
          width: 100%;
          height: 100%;
          display: block;
        }
      }
      .QuickMenu_My,
      .QuickMenu_DeliveryCheck,
      .QuickMenu_Cart,
      .QuickMenu_ViewProduct {
        width: 150px;
        overflow: hidden;
        padding: 0;
        display: flex;
        cursor: pointer;

        .QuickMy_txt,
        .QuickDeliveryCheck_txt,
        .QuickCart_txt,
        .QuickViewProduct_txt {
          width: 0px;
          font-size: 14px;
          line-height: 50px;
          text-align: center;
          cursor: pointer;
        }
        .QuickMy_img,
        .QuickDeliveryCheck_img,
        .QuickCart_img,
        .QuickViewProduct_img {
          width: 50px;
          padding: 5px;
          box-sizing: border-box;

          img {
            width: 47px;
            height: auto;
            display: block;
            margin-left: -11%;
            margin-top: -5%;
          }
        }
        .QuickDeliveryCheck_img img,
        .QuickCart_img img,
        .QuickViewProduct_img img {
          width: 39px;
          margin: 0;
        }
      }




      .QuickMenu_UP_Down {
        position: absolute;
        bottom: 0;
        width: 50px;
        height: 50px;
        line-height: 25px;
        font-size: 18px;
        border-bottom: none;
        padding: 0;
        text-align: center;
        color: #777;
        overflow: hidden;
        cursor: pointer;
        box-sizing: border-box;

        .QuickUp {
          width: 100%;
          height: 50%;
          border-bottom: 1px solid #ccc;
          box-sizing: border-box;
          overflow: hidden;
        }
        .QuickUp a,
        .QuickDown a {
          color: #777;
          display: block;
        }
      }
    }
  }

    .Q_menu {
      width: 50px;
      height: 50px;
    }
  }







/* Header =================================== */

$PointColor: #002053;
$SmallFont : 14px;
$MidleFont : 16px;
$LageFont : 18px;
$TitFont : 32px;

#header {
  position: absolute;
  left: 0;
  top: 0;
  z-index: 10;
  width: 100%;
  height: 100px;
  background-color: #fff;

  .header_outer{
    width: 100%;
    height: 100px;
  }

  .header_box {
    display: flex;
    width: 1660px;
    height: 100px;
    margin: 0 auto;
    flex-direction: row;
    justify-content: space-between;
    margin-top: 0.5px;

    .logo {
      width: 150px;
      height: 100px;
      line-height: 105px;
      text-align: center;
      padding: 10px;
      box-sizing: border-box;
    }

    .sht_header .each_menu_ul,
    .mainMenu .mainMenu_ul {
      width: 400px;
      height: 100px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      font-size: 20px;
      font-weight: bold;

      li:nth-child(4) {
        width: 1px;
        height: 15px;
        border-right: 1px solid #000;
        box-sizing: border-box;
      }

      li {
        height: 100px;
        line-height: 100px;
      }

      a {
        color: #000;
        display: block;
      }
    }
  }
}


/* GNB ========== */
#header .gnb {
  .gnb_box {
    .gnb_Menu {
      width: 220px;
      height: 100px;
      display: flex;
      flex-wrap: wrap;
      flex-direction: row;
      justify-content: space-around;
      align-items: center;

      .gnb_Menu_map picture {
        padding: 5px;
        text-align: center;
      }
    }
  }
}



/* 메인 메뉴 SHEET HEADER 영역*/
.mainMenu_Sheet {
  position: absolute;
  top: 0.5px;
  left: 0;
  z-index: 100;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  /* background 블러처리 */
  background-color: rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(8px);

  .main_menu_sht_outer {
    width: 100%;
    height: 65vh;
    background-color: #fff;
    display: flex;
    justify-content: center;

    .main_menu_sht {
      display: flex;
      z-index: 100;
      flex-direction: row;
      flex-wrap: wrap;
      justify-content: center;
      width: 1660px;
      height: 65vh;
      background-color: #fff;
      background-blend-mode: hue;

      /* SHEET 메뉴 영역 */
      .sht_header {
        display: flex;
        width: 1660px;
        height: 100px;
        margin: 0 auto;
        flex-direction: row;
        justify-content: space-between;
        box-sizing: border-box;

        .sht_logo {
          width: 150px;
          height: 100px;
          line-height: 105px;
          text-align: center;
          padding: 10px;
          box-sizing: border-box;
        }

        .each_menu_ul {
          width: 400px;
          height: 100px;
          display: flex;
          justify-content: space-between;
          align-items: center;
          font-size: 20px;
          font-weight: bold;

          li {
            height: 25px;
            line-height: 25px;
          }

          // sheet 메뉴 상단 hover color 변경
          li:hover {
            border-bottom: 2px solid $PointColor;
            box-sizing: border-box;
          }
        }

        .sht_gnb_Menu {
          width: 220px;
          height: 100px;
          display: flex;
          flex-wrap: wrap;
          flex-direction: row;
          justify-content: space-around;
          align-items: center;

          .sht_gnb_Menu_map picture {
            padding: 5px;
            text-align: center;
          }
        }
      }
    }
  }
}


/* SHEET 영역 내 HEADER li(bar) 영역*/
.mainMenu_Sheet {
  .main_menu_sht {
    .sht_header {
      .each_menu_ul {
        li {
          a {
            color: #000;
          }
        }

        li:nth-child(4) {
          position: relative;
          z-index: -1;
          display: block !important;
          width: 1px;
          height: 15px;
          border-right: 1px solid #000;
          box-sizing: border-box;
        }
      }
    }
  }
}



/* SHEET MENU LIST BOX (맨앞,맨뒤영역)*/
.sheet_box {
  width: 1660px;
  position: absolute;
  top: 100px;
  display: flex;

  .sht_firstBox,
  .sht_lastBox {
    display: flex;
    width: 13.3333%;
    height: 500px;
    flex-direction: row;
    flex-wrap: wrap;
    box-sizing: border-box;
    align-content: flex-start;
    padding: 30px 20px;
    box-sizing: border-box;

    li:first-child {
      width: 100%;
      height: 50px;
      line-height: 35px;
      font-weight: bold;
      font-size: $LageFont;
    }

    li:nth-child(6) {
      margin-top: 20px;
    }

    li {
      width: 100%;
      height: 35px;
      line-height: 35px;
    }

    li:hover a {
      color: $PointColor;
      font-weight: bold;
    }

    li:first-child:hover a {
      color: #000;
    }
  }
}




/* SHEET MENU LIST BOX (중간영역)*/
.sht_box_1,
.sht_box_2,
.sht_box_3,
.sht_box_4 {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-content: flex-start;
  width: 13.3333%;
  height: 500px;
  padding: 30px 25px;
  box-sizing: border-box;


  li:first-child {
    height: 50px;
    font-weight: bold;
    font-size: $LageFont;
  }

  li {
    width: 100%;
    height: 35px;
    line-height: 35px;
  }

  li:hover a {
    color: $PointColor;
    font-weight: bold;
  }

  li:first-child:hover a {
    color: #000;
  }
}



/* SHEET MENU LIST BOX (영역사이 bar)*/
.bar_sht {
  width: 10%;
  height: 390px;
  box-sizing: border-box;
  margin: 40px 0;

  .bar_1:first-child {
    width: 50%;
    height: 100%;
    border-right: 1px solid #ccc;
    box-sizing: border-box;
  }

  .bar_2:first-child {
    width: 50%;
    height: 100%;
    border-right: 1px solid #ccc;
    box-sizing: border-box;
  }
}

.Men_bar_sht_1,
.Men_bar_sht_2 {
  height: 335px;
}

.Kids_bar_sht_1,
.Kids_bar_sht_1 {
  height: 415px;
}




.mainMenu_Sheet {
  display: none;
}

// .Women_sheet_box {display: none;}
// .Men_sheet_box {display: none;}
// .Kids_sheet_box {display: none;} 




/* BRAND BOX */
.Brand_sheet_box {
  background-color: #fff;
  display: flex;

  .Brand_outer {
    width: 20%;
    height: 500px;
    padding: 10px;
    box-sizing: border-box;

    a {
      display: block;

      img {
        width: 100%;
        height: auto;
        display: block;
      }

      p {
        width: 100%;
        height: 30px;
        line-height: 30px;
        text-align: center;
        font-weight: bold;
        padding-top: 15px;
      }
    }
  }
}

.hd_fix {
  position: fixed;
  top: 0;
  left: 0;
  background-color: $PointColor;
  z-index: 9999;
}
























// ======================================
// ======================================
// ======================================

.detail_Top {
  width: 1420px;
  height: 940px;
  margin: 0 auto;
  padding-top: 100px;
  box-sizing: border-box;

  .detail_Img_Box {
    display: flex;
    justify-content: space-between;
    width: 100%;
    height: 800px;
    margin-top: 15px;
    box-sizing: border-box;

    .detail_Box_Left {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;
      justify-content: space-between;
      width: 1050px;
      height: 800px;

      .detail_Thumb_Img_Left {
        width: 515px;
        height: 650px;
        box-sizing: border-box;
        overflow: hidden;

        video {
          margin-top: -20px;
          width: 100%;
          height: auto;
          display: block;
        }
      }

      .detail_Thumb_Img_Right {
        position: relative;
        width: 515px;
        height: 650px;
        background-image: url(./BACK_IMG/detail1.jpeg);
        background-size: cover;
        background-repeat: no-repeat;

        .Thumb_ImgBox {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: space-evenly;
          position: absolute;
          top: 0;
          left: 0;
          width: 100px;
          height: 650px;
          background-color: rgba(0, 0, 0, 0.1);
          backdrop-filter: blur(3px);

          .Th_img {
            width: 80px;
            height: 80px;
            background-color: skyblue;
            background-size: cover;
            background-repeat: no-repeat;
            cursor: pointer;
          }
          .Thumb_Img1 {
            background-image: url(./BACK_IMG/detail1_Thumb.jpeg);
          }
          .Thumb_Img2 {
            background-image: url(./BACK_IMG/detail2_Thumb.jpeg);
          }
          .Thumb_Img3 {
            background-image: url(./BACK_IMG/detail3_Thumb.jpeg);
          }
          .Thumb_Img4 {
            background-image: url(./BACK_IMG/detail4_Thumb.jpeg);
          }
          .Thumb_Img5 {
            background-image: url(./BACK_IMG/detail5_Thumb.jpeg);
          }
          .Thumb_Img6 {
            background-image: url(./BACK_IMG/detail6_Thumb.jpeg);
          }
          .Thumb_Img7 {
            background-image: url(./BACK_IMG/detail7_Thumb.jpeg);
          }
        }
      }

      .detail_Event {
        width: 1050px;
        height: 170px;
        padding-bottom: 20px;
        padding-top: 20px;
        box-sizing: border-box;

        .detail_Event_box {
          width: 100%;
          height: 100%;
          background-image: url(./BACK_IMG/detail_Event.jpg);
          background-size: cover;
          background-repeat: no-repeat;

          a {
            width: 100%;
            height: 100%;
            display: block;
          }
        }
      }
    }





    .detail_Box_Right {
      width: 350px;
      height: 800px;

      .Right_Content {
        width: 100%;
        height: 100px;
        padding: 10px;
        // border-bottom: 1px solid #aaa; //시험경계선
        box-sizing: border-box;
      }

      .Right_category {
        height: 50px;
        padding: 10px;
        color: #777;

        ul {
          li:first-child {
            padding-left: 0;
          }

          li {
            font-size: $SmallFont;
            padding: 0 10px;
            padding-top: 5px;
            box-sizing: border-box;
            float: left;
          }

          li:nth-child(2) {
            width: 1px;
            height: 12px;
            border-right: 1px solid #777;
            box-sizing: border-box;
            padding: 0;
            margin-top: 10px;
          }
        }
      }

      .Right_ProductName {
        height: 80px;
        padding-top: 20px;
      }

      .Right_ProductColor {
        height: 120px;
        input {display: none;}
        h4 {
          width: 100%;
          height: 30px;
          margin-bottom: 5px;
          float: left;
        }

        .Product_FineGreen,
        .Product_HighRed {

          label {
            display: block;
            width: 50px;
            height: 50px;
            background-image: url(./BACK_IMG/Detail_FineGreen.png);
            margin-right: 15px;
            cursor: pointer;
            float: left;
          }
          label:hover {
            scale: 1.1;
            transition: 0.3s;
          }
        }
        .Product_HighRed label {
          background-image: url(./BACK_IMG/Detail_Red.png);
        }

      }
      .Right_ProductSize {
        height: 100px;

        h4 {
          width: 150px;
          height: 30px;
          margin-bottom: 5px;
          float: left;
        }
        h4:nth-child(2) {
          width: 150px;
          float: right;
          text-align: right;
          margin-right: 5px;
          text-decoration: underline;
          font-size: 12px;
          font-weight: normal;
          color: #777;
          cursor: pointer;
        }

        ul {
          li {
            input {display: none;}
            label {
              display: block;
              width: 55px;
              height: 45px;
              line-height: 45px;
              float: left;
              margin-right: 10px;
              font-size: $SmallFont;
              border-radius: 10px;
              border: 1px solid #000;
              box-sizing: border-box;
              text-align: center;          
              margin-bottom: 10px;
              cursor: pointer;
            }
          }
        }

      }

      .Right_Select_Number {
        .Select_NumberBox {
          position: relative;
          width: 100%;
          height: 50px;
          margin-top: 15px;
          border: 1px solid #777;
          border-radius: 30px;
          overflow: hidden;

          button {
            width: 50px;
            height: 50px;
            box-sizing: border-box;
            border: none;
            background-color: #fff;
            cursor: pointer;
            margin: 0;
            padding: 0;
            font-weight: bold;
            font-size: 25px;
            float: left;
          }
          #num_Plus {
            float: right;
          }

          input {
            position: absolute;
            top: 5px;
            left: 50%;
            transform: translateX(-50%);
            width: 200px;
            height: 40px;
            outline: none;
            border: none;
            text-align: center;
            font-size: 16px;
          }
          input[type='number']::-webkit-outer-spin-button,
          input[type='number']::-webkit-inner-spin-button {
            -webkit-appearance: none;
            margin: 0;
          }
        }
      }

      .Right_Price {
        height: 90px;
        font-weight: bold;
        font-size: $LageFont;

        span {
          float: left;
        }
        span:last-child {
          float: right;
        }
      }

      .Right_btn {
        display: flex;
        justify-content: space-between;
        align-items: center;
        width: 350px;
        height: 80px;
        margin-right: 10px;
        box-sizing: border-box;

        .Right_Heart,
        .Right_Cart {

          input {display: none;}

          label {
            display: block;
            width: 160px;
            height: 50px;
            border-radius: 30px;
            box-sizing: border-box;
            display: flex;
            justify-content: center;
            align-items: center;
            border: 1px solid #777;
            cursor: pointer;
            background-color: #fff;

            img {
              width: 25px;
              height: 25px;
              display: block;
            }
          }
          label:hover img {
            scale: 1.2;
            transition: 0.3s;
          }
        }
      }

      .Right_Order {
        display: flex;
        align-items: center;
        padding-bottom: 0;
        height: 80px;

        input {display: none;}
        label {
          display: block;
          width: 330px;
          height: 70px;
          margin: 0 auto;
          line-height: 70px;
          text-align: center;
          background-color: #fff;
          border-radius: 40px;
          font-size: $LageFont;
          font-weight: bold;
          border: 1px solid #777;
          box-sizing: border-box;
          cursor: pointer;
        }
        label:hover {
          background-color: $PointColor;
          border: 1px solid $PointColor;
          color: #fff;
          transition: 0.3s;
        }
      }
    }
  }
}




// ======================================
// ======================================
// ======================================
.Tap_All,
.Tap_All2 {
  width: 1420px;
  height: 50px;
  margin: 0 auto;
  margin-bottom: -1px;

  ul {
    display: flex;
    width: 600px;
    height: 50px;
    margin-right: 820px;

    li{
      flex: 1;
      text-align: center;
      line-height: 50px;
      border: 1px solid #000;
      box-sizing: border-box;
      border-radius: 15px 15px 0px 0px;
      cursor: pointer;
      font-weight: bold;
      margin-left: -1px;
    }
    li:first-child {
      margin-left: 0;
      background-color: $PointColor;
      color: #fff;
    }
  }
}




// .Product_Infomation {display: none;}
.Exchange_Return {display: none;}
.delivery_Info {display: none;}



.Product_Infomation,
.Exchange_Return,
.delivery_Info {
  // display: none;  /* 임시 */
  width: 1420px;
  margin: 0 auto;
  
  .Box_All {
    box-sizing: border-box;

    h3 {
      padding: 20px;
      box-sizing: border-box;
      color: $PointColor;
    }

    dl {
      display: flex;
      border-bottom: 1px solid #eee;
      padding: 20px;
      box-sizing: border-box;

      dt {
        flex: 3;
        font-weight: bold;
      }
      dd {
        flex: 7;
        p.delivery_First {
          width: 240px;
          padding: 20px;
          background-color: #eee;
          border-radius: 15px;
          margin-bottom: 20px;
        }
        p {
          padding: 5px;
          box-sizing: border-box;
        }

      }
    }
  }
  .product_infoBox1,
  .Exchange_infoBox1,
  .delivery_infoBox1 {
    border-top: 1px solid $PointColor;
  }
  .Exchange_infoBox1,
  .delivery_infoBox1 {
    padding-top: 20px;
  }
  .product_info .product_infoBoxNo1.Box_All {
    display: flex;
    border-bottom: 1px solid #eee;
    box-sizing: border-box;

    .product_infoBox1 {
      width: 100%;

      dl {
        display: flex;
        padding-top: 40px;

        dt {
          flex: 3;
          font-weight: bold;
          font-size: 18px;
        }

        dd {
          flex: 7;
          
          p {
            padding: 5px 0;
          }
        }
      }
    }
  }


  .product_infoBoxNo2 {
    display: flex;
    flex-direction: column;

    h3.h3_Title {
      padding: 20px;
      margin-bottom: 15px;
      box-sizing: border-box;
    }

      dl {
        display: flex;
        box-sizing: border-box;

        dt {
          flex: 3;
        }

        dd {
          flex: 7;

          a {
            text-decoration: underline;
          }
        }
      }
    }
    .product_infoBoxNo3,
    .product_infoBoxNo4 {
      display: flex;
      flex-direction: column;
      
      h3 {
        padding: 40px 0px 0px 20px;
        box-sizing: border-box;
      }
      h3.h3_Title {
        margin-top: 20px;
      }
      dl {
        display: flex;
        box-sizing: border-box;

        dt {
          flex: 3;
          height: 100%;
          padding: 10px 0;
          box-sizing: border-box;
          font-size: 18px;
          font-weight: bold;
        }
        dd {
          flex: 7;
          padding: 10px 0;
          box-sizing: border-box;

          p {
            padding: 10px 0;
          }
        }
      }
    }
}






