// ================================
// ================================

.Tap_All2 {
  position: relative;
  z-index: 1;
  margin-top: 150px;
}
.QnA{display: none;}
// .QnA{display: none;}
.Review,
.QnA {
  position: relative;
  width: 1420px;
  margin: 0 auto;
  border: 1px solid #eee;
  box-sizing: border-box;

  .Review_Top,
  .QnA_Top {
    display: flex;
    width: 100%;
    height: 315px;
    padding: 15px;
    box-sizing: border-box;

    .Review_Img,
    .QnA_Top_Img {
      flex: 1.5;
      height: 272px;
      background-image: url(./BACK_IMG/detail1.jpeg);
      background-size: cover;
      margin-right: 20px;
    }
    .Review_sideContent,
    .QnA_Top_Right {
      flex: 8.5;
      height: 270px;

      .Review_RightView {
        display: flex;
